import pytest
from django.urls import reverse
from rest_framework.test import APIClient
from rest_framework import status
from rest_framework_simplejwt.tokens import RefreshToken
from customers.models import Customer
from django.contrib.auth import get_user_model

User = get_user_model()


@pytest.fixture
def api_client():
    return APIClient()


@pytest.fixture
def user():
    return User.objects.create_user(
        username='testuser',
        email='test@example.com',
        password='testpass123'
    )


@pytest.fixture
def authenticated_client(api_client, user):
    """Return an authenticated API client"""
    refresh = RefreshToken.for_user(user)
    api_client.credentials(HTTP_AUTHORIZATION=f'Bearer {refresh.access_token}')
    return api_client


@pytest.fixture
def customer():
    return Customer.objects.create(
        name='Test Customer',
        phone_number='1234567890',
        email='customer@example.com'
    )


@pytest.mark.django_db
class TestCustomerViewSet:
    def test_list_customers(self, authenticated_client, customer):
        """Test listing customers"""
        url = reverse('customer-list')
        response = authenticated_client.get(url)
        assert response.status_code == status.HTTP_200_OK
        assert len(response.data['results']) > 0

    def test_create_customer(self, authenticated_client):
        """Test creating a new customer"""
        url = reverse('customer-list')
        data = {
            'name': 'New Customer',
            'phone_number': '9876543210',
            'email': 'newcustomer@example.com'
        }
        response = authenticated_client.post(url, data, format='json')
        assert response.status_code == status.HTTP_201_CREATED
        assert Customer.objects.filter(name='New Customer').exists()

    def test_retrieve_customer(self, authenticated_client, customer):
        """Test retrieving a single customer"""
        url = reverse('customer-detail', kwargs={'pk': customer.pk})
        response = authenticated_client.get(url)
        assert response.status_code == status.HTTP_200_OK
        assert response.data['id'] == customer.id

    def test_update_customer(self, authenticated_client, customer):
        """Test updating a customer"""
        url = reverse('customer-detail', kwargs={'pk': customer.pk})
        data = {'name': 'Updated Customer'}
        response = authenticated_client.patch(url, data, format='json')
        assert response.status_code == status.HTTP_200_OK
        customer.refresh_from_db()
        assert customer.name == 'Updated Customer'

    def test_delete_customer(self, authenticated_client, customer):
        """Test deleting a customer"""
        url = reverse('customer-detail', kwargs={'pk': customer.pk})
        response = authenticated_client.delete(url)
        assert response.status_code == status.HTTP_204_NO_CONTENT
        assert not Customer.objects.filter(pk=customer.pk).exists()

    def test_search_customers(self, authenticated_client, customer):
        """Test searching customers"""
        url = reverse('customer-search')
        response = authenticated_client.get(url, {'q': 'Test'})
        assert response.status_code == status.HTTP_200_OK
        assert len(response.data) > 0

    def test_unauthenticated_access(self, api_client):
        """Test that unauthenticated requests are rejected"""
        url = reverse('customer-list')
        response = api_client.get(url)
        assert response.status_code == status.HTTP_401_UNAUTHORIZED


