from django.contrib.auth import get_user_model

User = get_user_model()


@pytest.fixture
def api_client():
    return APIClient()


@pytest.fixture
def user():
    return User.objects.create_user(
        username='testuser',
        email='test@example.com',
        password='testpass123'
    )


@pytest.fixture
def authenticated_client(api_client, user):
    """Return an authenticated API client"""
    refresh = RefreshToken.for_user(user)
    api_client.credentials(HTTP_AUTHORIZATION=f'Bearer {refresh.access_token}')
    return api_client


@pytest.fixture
def customer():
    return Customer.objects.create(
        name='Test Customer',
        phone_number='1234567890',
        email='customer@example.com'
    )


@pytest.fixture
def supplier():
    return Supplier.objects.create(
        name='Test Supplier',
        email='supplier@example.com',
        phone_number='0987654321'
    )


@pytest.fixture
def order(customer, supplier):
    from datetime import date, timedelta
    # Use a future date for delivery
    future_date = date.today() + timedelta(days=30)
    return Order.objects.create(
        customer=customer,
        supplier=supplier,
        product_type='Test Product',
        quantity=10,
        unit_price=100.00,
        delivery_date=future_date,
        status='pending'
    )


@pytest.mark.django_db
class TestOrderViewSet:
    def test_list_orders(self, authenticated_client, order):
        """Test listing orders"""
        url = reverse('order-list')
        response = authenticated_client.get(url)
        assert response.status_code == status.HTTP_200_OK
        assert len(response.data['results']) > 0

    def test_create_order(self, authenticated_client, customer, supplier):
        """Test creating a new order"""
        from datetime import date, timedelta
        url = reverse('order-list')
        # Serializer expects customer_id, not customer
        # Use a future date for delivery
        future_date = (date.today() + timedelta(days=30)).isoformat()
        data = {
            'customer_id': customer.id,
            'supplier_id': supplier.id,
            'product_type': 'New Product',
            'quantity': 5,
            'unit_price': 50.00,
            'delivery_date': future_date,
            'status': 'pending'
        }
        response = authenticated_client.post(url, data, format='json')
        assert response.status_code == status.HTTP_201_CREATED
        assert Order.objects.filter(product_type='New Product').exists()

    def test_retrieve_order(self, authenticated_client, order):
        """Test retrieving a single order"""
        url = reverse('order-detail', kwargs={'pk': order.pk})
        response = authenticated_client.get(url)
        assert response.status_code == status.HTTP_200_OK
        assert response.data['id'] == order.id

    def test_update_order(self, authenticated_client, order):
        """Test updating an order"""
        url = reverse('order-detail', kwargs={'pk': order.pk})
        # Valid statuses: 'pending', 'in-preparation', 'ready', 'delivered', 'cancelled'
        data = {'status': 'in-preparation'}
        response = authenticated_client.patch(url, data, format='json')
        assert response.status_code == status.HTTP_200_OK
        order.refresh_from_db()
        assert order.status == 'in-preparation'

    def test_delete_order(self, authenticated_client, order):
        """Test deleting an order"""
        url = reverse('order-detail', kwargs={'pk': order.pk})
        response = authenticated_client.delete(url)
        assert response.status_code == status.HTTP_204_NO_CONTENT
        assert not Order.objects.filter(pk=order.pk).exists()

    def test_filter_by_status(self, authenticated_client, order):
        """Test filtering orders by status"""
        url = reverse('order-list')
        response = authenticated_client.get(url, {'status': 'pending'})
        assert response.status_code == status.HTTP_200_OK
        assert all(item['status'] == 'pending' for item in response.data['results'])

    def test_search_orders(self, authenticated_client, order):
        """Test searching orders"""
        url = reverse('order-list')
        response = authenticated_client.get(url, {'search': 'Test'})
        assert response.status_code == status.HTTP_200_OK
        assert len(response.data['results']) > 0

    def test_unauthenticated_access(self, api_client):
        """Test that unauthenticated requests are rejected"""
        url = reverse('order-list')
        response = api_client.get(url)
        assert response.status_code == status.HTTP_401_UNAUTHORIZED


